import csvpath ='/Users/andrewgiannettino/Documents/Python/python-challenge/PyBank/Resources/budget_data.csv'# Total Number of Monthswith open(path) as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")        for row in csvreader:        totalmonths = len(csvfile.readlines())            print(totalmonths) # Total Net Profit/Losssum = 0with open(path) as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    for rows in csvreader:                sum = sum + int(rows[1])print(sum)# Financial Analysisf=open("Financial-Analysis.txt", "w")f.write("Total Months: ")f.write(str(totalmonths))f.write("\n")f.write("Total: ")f.write("$")f.write(str(sum))f.write("\n")f.write("Average Change:")f.write("\n")f.write("Greatest Increase in Profit:")f.write("\n")f.write("Greatest Decrease in Profits:")f=open("Financial-Analysis.txt", "r")print(f.read())            