import csvpath ='/Users/andrewgiannettino/Documents/Python/python-challenge/PyBank/Resources/budget_data.csv'total_months=[]net_change_list=[]month_of_change=[]prev_net=0greatest_increase=0greatest_decrease=0greatest_increase_month=[]greatest_decrease_month=[]# Total Number of Monthswith open(path) as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")        for row in csvreader:        totalmonths = len(csvfile.readlines())                  sum = 0with open(path) as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    for row in csvreader:                sum = sum + int(row[1])#Average Changewith open(path) as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    for row in csvreader:        net_change= int(row[1]) - prev_net        prev_net = int(row[1])        net_change_list+=[net_change]        month_of_change+=[row[0]]#Greatest Increase             if net_change > greatest_increase:            greatest_increase_month = row[0]             greatest_increase = net_change#Greatest Decrease                if net_change < greatest_decrease:            greatest_decrease_month = row[0]            greatest_decrease = net_changeoutput = (    f"Financial Analysis\n"    f"------------------\n"    f"Total Months: {totalmonths}\n"    f"Total: ${sum}\n"    f"Average Change: ${net_change:.2f}\n"    f"Greatest Increase In Profits: {greatest_increase_month} (${greatest_increase})\n"    f"Greatest Decrease In Profits: {greatest_decrease_month} (${greatest_decrease})\n"    )print(output)with open('/Users/andrewgiannettino/Documents/Python/python-challenge/PyBank/Analysis/Financial-Analysis.txt' , "w") as txt_file:    txt_file.write(output)# Financial Analysis            