import csv path = '/Users/andrewgiannettino/Documents/Python/python-challenge/PyPoll/Resources/election_data.csv'stockham = 'Charles Casper Stockham'stockham_votes=0stockham_percentage=0degette = 'Diana DeGette'degette_votes=0degettes_percentage=0doane='Raymon Anthony Doane'doane_votes=0doane_percentage=0with open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)#total number of votes submitted    for row in csvreader:           total_votes = len(csvfile.readlines())#Charles Casper Stockham Voteswith open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)    for row in csvreader:        if stockham in row:            stockham_votes = stockham_votes + 1            stockham_percentage=(stockham_votes/total_votes) * 100            stockham_percentage=round(stockham_percentage, 2)#Diana DeGette Voteswith open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)    for row in csvreader:        if degette in row:            degette_votes = degette_votes + 1            degette_percentage=(degette_votes/total_votes) * 100            degette_percentage=round(degette_percentage, 2)#Raymon Anthony Doane Voteswith open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)    for row in csvreader:        if doane in row:            doane_votes = doane_votes + 1            doane_percentage=(doane_votes/total_votes) * 100            doane_percentage=round(doane_percentage, 2)#winner result        winner=max(doane_votes, stockham_votes, degette_votes)        if winner == 11606:            winner = str("Rayman Anthony Doane")                        elif winner == 85213:            winner += str("Charles Casper Stockham")                        elif winner == 272892:            winner = str("Diana Degette")output = (    f"Election Results\n"    f"----------------\n"    f"Total Votes: {total_votes}\n"    f"----------------\n"    f"Charles Casper Stockham: {stockham_percentage}% ({stockham_votes})\n"    f"Diana DeGette: {degette_percentage}% ({degette_votes})\n"    f"Raymon Anthony Doane: {doane_percentage}% ({doane_votes})\n"    f"----------------\n"    f"Winner: {winner}"    )print(output)with open('/Users/andrewgiannettino/Documents/Python/python-challenge/PyPoll/Analysis/Election Results.txt' , "w") as txt_file:    txt_file.write(output)