import csv path = '/Users/andrewgiannettino/Documents/Python/python-challenge/PyPoll/Resources/election_data.csv'stockham = 'Charles Casper Stockham'stockham_votes=0stockham_percentage=0degette = 'Diana DeGette'degette_votes=0degettes_percentage=0doane='Raymon Anthony Doane'doane_votes=0doane_percentage=0with open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)#total number of votes submitted    for row in csvreader:           total_votes = len(csvfile.readlines())print(total_votes)#Charles Casper Stockham Voteswith open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)    for row in csvreader:        if stockham in row:            stockham_votes = stockham_votes + 1            stockham_percentage=(stockham_votes/total_votes) * 100            #Diana DeGette Voteswith open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)    for row in csvreader:        if degette in row:            degette_votes = degette_votes + 1            degette_percentage=(degette_votes/total_votes) * 100#Raymon Anthony Doane Voteswith open(path) as csvfile:    csvreader=csv.reader(csvfile, delimiter=',')    csv_header = next(csvreader)    for row in csvreader:        if doane in row:            doane_votes = doane_votes + 1            doane_percentage=(doane_votes/total_votes) * 100            #winner resultprint(stockham_votes)print(stockham_percentage)        print(degette_votes)print(degette_percentage)print(doane_votes)print(doane_percentage)